import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { fetchQuestionsApi } from '../services/fetchAPI';

class Questions extends Component {
  state = {
    // isCorrect: false,
    indexQuestion: 0,
    arrayQuestions: [],
    arrayAnswers: [],
    isLoading: true,
  };

  componentDidMount() {
    this.fetchQuestions();
  }

  fetchQuestions = async () => {
    const token = localStorage.getItem('token');
    const data = await fetchQuestionsApi(token);
    this.setState({
      isLoading: false,
      arrayQuestions: data,
    }, this.shuffleAnswers);
  };

  shuffleAnswers = () => {
    const { arrayQuestions, indexQuestion } = this.state;
    const NUMBER = 0.5;
    const allAnswers = [...arrayQuestions[indexQuestion].incorrect_answers,
      arrayQuestions[indexQuestion].correct_answer];
    allAnswers.sort(() => Math.random() - NUMBER);
    setArray(allAnswers);
    this.setState({
      arrayAnswers: allAnswers,
    });
  };

  render() {
    const { indexQuestion, isLoading, arrayQuestions, arrayAnswers } = this.state;
    console.log(arrayAnswers);
    return (
      <div>
        { (isLoading) ? <p>Carregando...</p>
          : <p>{arrayQuestions[indexQuestion].question }</p>}
      </div>
    );
  }
}

Questions.propTypes = {
  questions: PropTypes.arrayOf(
    PropTypes.shape({}),
  ).isRequired,
}.isRequired;

const mapStateToProps = (state) => ({
  ...state.player,
});

export default connect(mapStateToProps)(Questions);
