import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { resetScore } from '../redux/actions/gameAction';

class Ranking extends Component {
  state = {
    listPlayers: [],
    activeRanking: false,
  };

  componentDidMount() {
    const getLocalStorage = JSON.parse(localStorage.getItem('ranking'));
    if (!getLocalStorage) {
      this.setState({ activeRanking: false });
    } else {
      this.setState({
        listPlayers: getLocalStorage.sort((a, b) => b.score - a.score),
        activeRanking: true,
      });
    }
  }

  handleButtonHome = () => {
    const { history, dispatch } = this.props;
    dispatch(resetScore());
    history.push('/');
  };

  render() {
    const { listPlayers, activeRanking } = this.state;
    return (
      <div>
        <h2 data-testid="ranking-title">Ranking</h2>
        { activeRanking ? listPlayers.map((player, index) => (
          <div key={ index }>
            <img
              alt="Avatar do usuÃ¡rio"
              src={ player.gravatarEmail }
            />
            <p data-testid={ `player-score-${index}` }>{ player.score }</p>
            <p data-testid={ `player-name-${index}` }>{ player.name }</p>
          </div>
        )) : <p>Nenhum player</p>}
        <button
          onClick={ this.handleButtonHome }
          data-testid="btn-go-home"
          type="button"
        >
          Go Home
        </button>
      </div>
    );
  }
}

Ranking.propTypes = {
  history: PropTypes.shape({
    push: PropTypes.func,
  }),
  dispatch: PropTypes.func,
}.isRequired;

export default connect()(Ranking);
