import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { fetchQuestionsApi } from '../services/fetchAPI';

class Questions extends Component {
  state = {
    // isCorrect: false,
    indexQuestion: 0,
    arrayQuestions: [],
    arrayAnswers: [],
    // isLoading: true,
  };

  componentDidMount() {
    this.fetchQuestions();
  }

  fetchQuestions = async () => {
    // const { isLoading } = this.state;
    const token = localStorage.getItem('token');
    const data = await fetchQuestionsApi(token);
    this.setState({
      isLoading: false,
      arrayQuestions: data,
    });
  };

  render() {
    // const { questions } = this.props;
    const { indexQuestion, isLoading, arrayQuestions } = this.state;
    // let correctAnswer = '';
    // let arrayAnswers = [];
    // if (questions.length !== 0) {
    //   correctAnswer = questions[indexQuestion].correct_answer;
    //   arrayAnswers = [...questions[indexQuestion].incorrect_answers, correctAnswer];
    // }
    return (
      <div>
        {/* { (isLoading) ? <p>Carregando...</p>
          : <p>{arrayQuestions[indexQuestion].question }</p>} */}
      </div>
    );
  }
}

Questions.propTypes = {
  questions: PropTypes.arrayOf(
    PropTypes.shape({}),
  ).isRequired,
}.isRequired;

const mapStateToProps = (state) => ({
  ...state.player,
});

export default connect(mapStateToProps)(Questions);
