import React, { Component } from 'react';
import PropTypes from 'prop-types';

class Ranking extends Component {
  state = {
    listPlayers: [],
  };

  componentDidMount() {
    const listFromStorage = JSON.parse(localStorage.getItem('ranking'));
    const listPlayers = listFromStorage.sort((a, b) => b.score - a.score);
    this.setState({ listPlayers });
  }

  handleButtonHome = () => {
    const { history } = this.props;
    history.push('/');
  };

  render() {
    const { listPlayers } = this.state;
    return (
      <div>
        <h2 data-testid="ranking-title">Ranking</h2>
        <button
          onClick={ this.handleButtonHome }
          data-testid="btn-go-home"
          type="button"
        >
          Go Home
        </button>
      </div>
    );
  }
}

Ranking.propTypes = {
  history: PropTypes.shape({
    push: PropTypes.func,
  }),
}.isRequired;

export default Ranking;
