import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

class Questions extends Component {
  state = {
    // isCorrect: false,
    indexQuestion: 0,
    arrayAnswers: [],
  };

  render() {
    const { questions } = this.props;
    const { indexQuestion } = this.state;
    let correctAnswer = '';
    const arrayAnswers = [];
    if (questions.length !== 0) {
      correctAnswer = questions[indexQuestion].correct_answer;
      arrayAnswers = [...questions[indexQuestion].incorrect_answers, correctAnswer];
    }
    return (
      <div>
        { (questions.length === 0) ? <p>Carregando...</p>
          : (
            <div>
              <h1>{ questions[indexQuestion].question }</h1>
              { arrayAnswers.map((answer, index) => (
                <button key={ index }>{answer}</button>
              )) }
            </div>
          ) }
      </div>
    );
  }
}

Questions.propTypes = {
  questions: PropTypes.arrayOf(
    PropTypes.shape({}),
  ).isRequired,
}.isRequired;

const mapStateToProps = (state) => ({
  ...state.player,
});

export default connect(mapStateToProps)(Questions);
