const INITIAL_STATE = {
  name: '',
  assertions: '',
  score: 0,
  gravatarEmail: '',
};

const player = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case ACTION: {
    return {
      ...state,
      email: action.payload,
    };
  }
  default: return state;
  }
};

export default player;
