import { AMOUNT_ASSERT, LOGIN_ACESS, SCORE_USER } from '../actions';

const INITIAL_STATE = {
  name: '',
  assertions: '',
  score: 0,
  gravatarEmail: '',
  questions: [],
};

const player = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case LOGIN_ACESS: {
    return {
      ...state,
      name: action.payload.name,
      gravatarEmail: action.payload.email,
    };
  }

  case AMOUNT_ASSERT: {
    return {
      ...state,
      questions: action.payload,
    };
  }

  case SCORE_USER: {
    return {
      ...state,
      score: state.score + action.payload,
    };
  }

  default: return state;
  }
};

export default player;
