import React, { Component } from 'react';
import PropTypes from 'prop-types';

import { fetchQuestionsApi } from '../services/fetchAPI';
import '../styles/Questions.css';

class Questions extends Component {
  state = {
    // isCorrect: false,
    indexQuestion: 0,
    arrayQuestions: [],
    arrayAnswers: [],
    isLoading: true,
    correctAnswer: '',
    correct: false,
    incorrect: false,
    enabledNextButton: false,
    complete: false,
  };

  componentDidMount() {
    this.fetchQuestions();
  }

  fetchQuestions = async () => {
    const { history } = this.props;
    const { arrayQuestions, indexQuestion } = this.state;

    const token = localStorage.getItem('token');
    const data = await fetchQuestionsApi(token);

    const NUMBER = 3;
    const NUMBER_MATH = 0.5;

    if (data.response_code === NUMBER) {
      localStorage.removeItem('token');
      history.push('/');
    } else {
      this.setState({
        isLoading: false,
        arrayQuestions: data.results,
        arrayAnswers: allAnswers,
        correctAnswer: [...arrayQuestions[indexQuestion].incorrect_answers,
          arrayQuestions[indexQuestion].correct_answer]
          .sort(() => Math.random() - NUMBER_MATH),
      });
    }
  };

  handleNext = () => {
    const { indexQuestion } = this.state;
    const completeTask = 4;
    this.setState((prev) => ({
      indexQuestion: prev.indexQuestion + 1,
      correct: '',
      incorrect: '',
      enabledNextButton: false,
      complete: prev.indexQuestion === completeTask,
    }));
  };

  redirectToFeedback = () => {
    const { complete } = this.state;
    const { history } = this.props;
    if (complete) {
      history.push('/feedback');
    }
  };

  handleButtonOptions = () => {
    this.setState({
      correct: 'correct',
      incorrect: 'incorrect',
      enabledNextButton: true,
    });
  };

  render() {
    const { indexQuestion, isLoading, arrayQuestions, enabledNextButton,
      arrayAnswers, correctAnswer, correct, incorrect } = this.state;
    return (
      <div>
        { isLoading && <p>Carregando...</p> }
        { !isLoading && (
          <div>
            <p data-testid="question-category">
              {arrayQuestions[indexQuestion].category }
            </p>
            <p data-testid="question-text">{arrayQuestions[indexQuestion].question }</p>
            <div data-testid="answer-options">
              {
                arrayAnswers.map((answer, index) => (
                  <button
                    type="button"
                    onClick={ this.handleButtonOptions }
                    className={ answer === correctAnswer ? `${correct}`
                      : `${incorrect}` }
                    data-testid={ answer === correctAnswer ? 'correct-answer'
                      : `wrong-answer-${index}` }
                    key={ answer }
                  >
                    {answer}
                  </button>))
              }
            </div>
          </div>) }
        {
          enabledNextButton && (
            <button
              onClick={ this.handleNext }
              data-testid="btn-next"
              type="button"
            >
              Next
            </button>
          )
        }
      </div>
    );
  }
}

Questions.propTypes = {
  history: PropTypes.shape({
    push: PropTypes.func,
  }),
}.isRequired;

export default Questions;
