import React, { Component } from 'react';
import PropTypes from 'prop-types';

import { fetchQuestionsApi } from '../services/fetchAPI';
import '../styles/Questions.css';

class Questions extends Component {
  state = {
    indexQuestion: 0,
    arrayQuestions: [],
    arrayAnswers: [],
    isLoading: true,
    correctAnswer: '',
    correct: false,
    incorrect: false,
    enabledNextButton: false,
  };

  componentDidMount() {
    const { indexQuestion } = this.state;
    this.fetchQuestions(indexQuestion);
  }

  fetchQuestions = async (index) => {
    const { history } = this.props;
    const token = localStorage.getItem('token');
    const data = await fetchQuestionsApi(token);
    const newData = data.results;
    const CODE = 3;
    const SORT = 0.5;

    if (data.response_code === CODE) {
      localStorage.removeItem('token');
      history.push('/');
    } else {
      this.setState({
        isLoading: false,
        correctAnswer: newData[index].correct_answer,
        arrayQuestions: newData,
        arrayAnswers: [...newData[index].incorrect_answers,
          newData[index].correct_answer].sort(() => Math.random() - SORT),
      });
    }
  };

  handleButtonOptions = () => {
    this.setState({
      correct: 'correct',
      incorrect: 'incorrect',
      enabledNextButton: true,
    });
  };

  shuffleAnswers = () => {
    const { arrayQuestions, indexQuestion } = this.state;
    const NUMBER = 0.5;
    const allAnswers = [...arrayQuestions[indexQuestion].incorrect_answers,
      arrayQuestions[indexQuestion].correct_answer];
    allAnswers.sort(() => Math.random() - NUMBER);
    this.setState({
      arrayAnswers: allAnswers,
      correctAnswer: arrayQuestions[indexQuestion].correct_answer,
    });
  };

  render() {
    const { indexQuestion, isLoading, arrayQuestions, enabledNextButton,
      arrayAnswers, correctAnswer, correct, incorrect } = this.state;
    return (
      <div>
        { isLoading && <p>Carregando...</p> }
        { !isLoading && (
          <div>
            <p data-testid="question-category">
              {arrayQuestions[indexQuestion].category }
            </p>
            <p data-testid="question-text">{arrayQuestions[indexQuestion].question }</p>
            <div data-testid="answer-options">
              {
                arrayAnswers.map((answer, index) => (
                  <button
                    type="button"
                    onClick={ this.handleButtonOptions }
                    className={ answer === correctAnswer ? `${correct}`
                      : `${incorrect}` }
                    data-testid={ answer === correctAnswer ? 'correct-answer'
                      : `wrong-answer-${index}` }
                    key={ answer }
                  >
                    {answer}
                  </button>))
              }
            </div>
          </div>) }
        {
          enabledNextButton && <button data-testid="btn-next" type="button">Next</button>
        }
      </div>
    );
  }
}

Questions.propTypes = {
  history: PropTypes.shape({
    push: PropTypes.func,
  }),
}.isRequired;

export default Questions;
